// Project Euler - Problem 08: Largest product in a series
// Compile with: c99 euler-008.c -o euler-008 -Wall -Wextra -pedantic
// Windows: cl /W4 /nologo euler-008.c

#include <stdint.h>
#include <stdlib.h>
#include <stdio.h>
#include <time.h>

#define uint64 uint64_t

void euler008();
uint64 brute_force();
int char_to_int(char c);

char* input = "";

int main(void)
{
    puts("Project Euler - Problem 08: Largest product in a series");

    euler008();

    return EXIT_SUCCESS;
}

void euler008()
{
    float start = (float)clock() / CLOCKS_PER_SEC;

    uint64 ans = brute_force();

    float elapsed = (float)clock() / CLOCKS_PER_SEC - start;
    printf("Answer: %lu (elapsed: %fs)\n", ans, elapsed);
}

uint64 brute_force()
{
    int digits = 13;
    uint64 max_product = 0;

    for (int i = digits; input[i] != '\0'; i++)
    {
        uint64 current_product = 1;
        for (int d = 0; d < digits; d++)
        {
            current_product *= char_to_int(input[i-d]);
        }
        if (current_product > max_product)
        {
            max_product = current_product;
        }
    }
    return max_product;
}

int char_to_int(char c)
{
    if (c < 48 || c > 59) return -1;
    return c - 48;
}
